var zoomFactor = 0.1;
var frameName = webMI.query["targetFrame"];


webMI.trigger.connect("setFrame", function(e) {
	myFrame = e.value;
});

webMI.addEvent("clickarea_1", "mousedown", function(e) {
	var id = "clickarea_1";
	var value = true;
	return function(value) {
		webMI.gfx.setFill("button_stroke_1", "url(#linear_2)");
	} (value);
});

