			var defaultOptions = {
				renderTo: "",
				startAddress: "AGENT.OBJECTS",
				aggregateAddressPrefix: "AGENT.HISTORY.AGGREGATETEMPLATES",
				selectableTypes: [types.baseVariable, types.aggregateFunction],
				fontSize: 12,
