				busyIndicatorTolerance: 100,
				expandoOffset: 3,
				searchDepth: 100,
				navigationDepth: 2,
				displayConfig : null,
				imagePaths: {
					baseVariableImage: "/treeView/icons/baseVariable.svg",
					folderImage: "/treeView/icons/folder.svg",
					displayImage: "/treeView/icons_navigator/display.svg",
